def getScanParams():
    start = w.Estart.get()
    end = w.Eend.get()
    speed = w.Speed.get()
    try:
        start = float(start)
        end = float(end)
        speed = float(speed)
        valid = True
    except:
        start = 0
        end = 0
        speed = 0
        valid = False
    return start,end,speed,valid

