#*****************Scanning routines********************************
def XASScan(start,end,speed):
    global ScanRunning
    ScanRunning = True
    energy = np.linspace(start,end,1000)
    energytime = np.linspace(1,5,1000)
    Isample = np.random.rand(1010)
    Isampletime = np.linspace(1,5,1010)
    Imesh = np.random.rand(900)
    Imeshtime = np.linspace(1,5,900)
    SaveData(energy,energytime,Isample,Isampletime,Imesh,Imeshtime)
    ScanRunning = False

def SaveData(energy,energytime,Isample,Isampletime,Imesh,Imeshtime):

    i=0
    while True: # create group in loop
        groupname = GroupMask + str(i) 
        try:
            g1 = h5file.create_group(groupname)
            break
        except:
            i+=1

    energyset = g1.create_dataset("E_mono", data=energy)
    energytimeset = g1.create_dataset("E_mono_t", data=energytime)
    Isampleset = g1.create_dataset("I_sample", data=Isample,)
    Isampletimeset = g1.create_dataset("I_sample_t", data=Isampletime,)
    Imeshset = g1.create_dataset("I_mesh", data=Imesh,)
    Imeshtimeset = g1.create_dataset("I_mesh_t", data=Imeshtime,)

